package p_01_demo;

public class D_06_Schleife {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("------------ Scheliefe mit for-------------");
		for(int i=0;i<=10;i++) {
			System.out.println(i);
		}
		
		System.out.println("------------ Scheliefe mit while-------------");
		int x=1;
		while(x<=10) {
			System.out.println(x);
			x++;
		}
		
		

	}

}
